mapper.select:
	select count(SNo)
  from stutopic
  where SNo=#{SNo}

	select DISTINCT t.TopicID,t.TopicName
  from topic t,stutopic stut
  where t.TopicID=stut.TopicID and stut.SNo=#{SNo}

		select a.aid,m.mid,m.mpaddress
      from movieinfo m,alarminfo a,alarm_movie am
      where m.mid=am.mid and am.aid=a.aid and a.astatus=#{astatus}

		select p.*
      from pictureinfo p,positioninfo po
      where p.poid=po.poid and po.poname=#{poname}

		select p.*
      from pictureinfo p,positioninfo po
      where p.poid=po.poid and po.poname=#{arg0} and po.poid=#{arg1}

		select p.*
      from pictureinfo p,positioninfo po
      where p.poid=po.poid and po.poname=#{arg0} and p.pdate=#{arg1}

		select *
      from pictureinfo
      where  poid=#{arg1} and pdate=#{arg0}

		select p.*
      from pictureinfo p,positioninfo po
      where p.poid=po.poid and po.poname=#{arg0} and p.poid=#{arg1} and p.pdate=#{arg2}
